			case 1:
				$this->setNbUsuario($value);
				break;
			case 2:
				$this->setApUsuario($value);
				break;
			case 3:
				$this->setNuCedula($value);
				break;
			case 4:
				$this->setTxLogin($value);
				break;
			case 5:
				$this->setTxPassword($value);
				break;
			case 6:
				$this->setCoRol($value);
				break;
			case 7:
				$this->setInActivo($value);
				break;
		} // switch()
	}

	/**
	 * Populates the object using an array.
	 *
	 * This is particularly useful when populating an object from one of the
	 * request arrays (e.g. $_POST).  This method goes through the column
	 * names, checking to see whether a matching key exists in populated
	 * array. If so the setByName() method is called for that column.
	 *
	 * You can specify the key type of the array by additionally passing one
	 * of the class type constants BasePeer::TYPE_PHPNAME, BasePeer::TYPE_STUDLYPHPNAME,
	 * BasePeer::TYPE_COLNAME, BasePeer::TYPE_FIELDNAME, BasePeer::TYPE_NUM.
	 * The default key type is the column's phpname (e.g. 'AuthorId')
	 *
	 * @param      array  $arr     An array to populate the object from.
	 * @param      string $keyType The type of keys the array uses.
	 * @return     void
	 */
	public function fromArray($arr, $keyType = BasePeer::TYPE_PHPNAME)
	{
		$keys = T01UsuarioPeer::getFieldNames($keyType);

		if (array_key_exists($keys[0], $arr)) $this->setCoUsuario($arr[$keys[0]]);
		if (array_key_exists($keys[1], $arr)) $this->setNbUsuario($arr[$keys[1]]);
		if (array_key_exists($keys[2], $arr)) $this->setApUsuario($arr[$keys[2]]);
		if (array_key_exists($keys[3], $arr)) $this->setNuCedula($arr[$keys[3]]);
		if (array_key_exists($keys[4], $arr)) $this->setTxLogin($arr[$keys[4]]);
		if (array_key_exists($keys[5], $arr)) $this->setTxPassword($arr[$keys[5]]);
		if (array_key_exists($keys[6], $arr)) $this->setCoRol($arr[$keys[6]]);
		if (array_key_exists($keys[7], $arr)) $this->setInActivo($arr[$keys[7]]);
	}

	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(T01UsuarioPeer::DATABASE_NAME);

		if ($this->isColumnModified(T01UsuarioPeer::CO_USUARIO)) $criteria->add(T01UsuarioPeer::CO_USUARIO, $this->co_usuario);
		if ($this->isColumnModified(T01UsuarioPeer::NB_USUARIO)) $criteria->add(T01UsuarioPeer::NB_USUARIO, $this->nb_usuario);
		if ($this->isColumnModified(T01UsuarioPeer::AP_USUARIO)) $criteria->add(T01UsuarioPeer::AP_USUARIO, $this->ap_usuario);
		if ($this->isColumnModified(T01UsuarioPeer::NU_CEDULA)) $criteria->add(T01UsuarioPeer::NU_CEDULA, $this->nu_cedula);
		if ($this->isColumnModified(T01UsuarioPeer::TX_LOGIN)) $criteria->add(T01UsuarioPeer::TX_LOGIN, $this->tx_login);
		if ($this->isColumnModified(T01UsuarioPeer::TX_PASSWORD)) $criteria->add(T01UsuarioPeer::TX_PASSWORD, $this->tx_password);
		if ($this->isColumnModified(T01UsuarioPeer::CO_ROL)) $criteria->add(T01UsuarioPeer::CO_ROL, $this->co_rol);
		if ($this->isColumnModified(T01UsuarioPeer::IN_ACTIVO)) $criteria->add(T01UsuarioPeer::IN_ACTIVO, $this->in_activo);

		return $criteria;
	}

	/**
