	/**
	 * Declares an association between this object and a T02Rol object.
	 *
	 * @param      T02Rol $v
	 * @return     T01Usuario The current object (for fluent API support)
	 * @throws     PropelException
	 */
	public function setT02Rol(T02Rol $v = null)
	{
		if ($v === null) {
			$this->setCoRol(NULL);
		} else {
			$this->setCoRol($v->getCoRol());
		}

		$this->aT02Rol = $v;

		// Add binding for other direction of this n:n relationship.
		// If this object has already been added to the T02Rol object, it will not be re-added.
		if ($v !== null) {
			$v->addT01Usuario($this);
		}

		return $this;
	}


	/**
	 * Get the associated T02Rol object
	 *
	 * @param      PropelPDO Optional Connection object.
	 * @return     T02Rol The associated T02Rol object.
	 * @throws     PropelException
	 */
	public function getT02Rol(PropelPDO $con = null)
	{
		if ($this->aT02Rol === null && ($this->co_rol !== null)) {
			$c = new Criteria(T02RolPeer::DATABASE_NAME);
			$c->add(T02RolPeer::CO_ROL, $this->co_rol);
			$this->aT02Rol = T02RolPeer::doSelectOne($c, $con);
			/* The following can be used additionally to
			   guarantee the related object contains a reference
			   to this object.  This level of coupling may, however, be
			   undesirable since it could result in an only partially populated collection
			   in the referenced object.
			   $this->aT02Rol->addT01Usuarios($this);
			 */
		}
		return $this->aT02Rol;
	}

