		}

		$criteria->addJoin(array(T07ProductoPeer::CO_PROVEEDOR,), array(T06ProveedorPeer::CO_PROVEEDOR,), $join_behavior);

    foreach (sfMixer::getCallables('BaseT07ProductoPeer:doCount:doCount') as $callable)
    {
