	/**
	 * Selects a collection of T07Producto objects pre-filled with all related objects.
	 *
	 * @param      Criteria  $c
	 * @param      PropelPDO $con
	 * @param      String    $join_behavior the type of joins to use, defaults to Criteria::LEFT_JOIN
	 * @return     array Array of T07Producto objects.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doSelectJoinAll(Criteria $c, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{

    foreach (sfMixer::getCallables('BaseT07ProductoPeer:doSelectJoinAll:doSelectJoinAll') as $callable)
    {
      call_user_func($callable, 'BaseT07ProductoPeer', $c, $con);
    }


		$c = clone $c;

		// Set the correct dbName if it has not been overridden
		if ($c->getDbName() == Propel::getDefaultDB()) {
			$c->setDbName(self::DATABASE_NAME);
		}

		T07ProductoPeer::addSelectColumns($c);
		$startcol2 = (T07ProductoPeer::NUM_COLUMNS - T07ProductoPeer::NUM_LAZY_LOAD_COLUMNS);

		T06ProveedorPeer::addSelectColumns($c);
		$startcol3 = $startcol2 + (T06ProveedorPeer::NUM_COLUMNS - T06ProveedorPeer::NUM_LAZY_LOAD_COLUMNS);

		$c->addJoin(array(T07ProductoPeer::CO_PROVEEDOR,), array(T06ProveedorPeer::CO_PROVEEDOR,), $join_behavior);
		$stmt = BasePeer::doSelect($c, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = T07ProductoPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = T07ProductoPeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {
				$omClass = T07ProductoPeer::getOMClass();

				$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
				$obj1 = new $cls();
				$obj1->hydrate($row);
				T07ProductoPeer::addInstanceToPool($obj1, $key1);
			} // if obj1 already loaded

			// Add objects for joined T06Proveedor rows

			$key2 = T06ProveedorPeer::getPrimaryKeyHashFromRow($row, $startcol2);
			if ($key2 !== null) {
				$obj2 = T06ProveedorPeer::getInstanceFromPool($key2);
				if (!$obj2) {

					$omClass = T06ProveedorPeer::getOMClass();


					$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					T06ProveedorPeer::addInstanceToPool($obj2, $key2);
				} // if obj2 loaded

				// Add the $obj1 (T07Producto) to the collection in $obj2 (T06Proveedor)
				$obj2->addT07Producto($obj1);
			} // if joined row not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}


